<template>
  <img
    :src="icon"
    :data-class="encounter.characterClass"
    :height="size"
    :width="size"
    loading="lazy"
  />
</template>

<script lang="ts">
import { computed, defineComponent, PropType } from 'vue';

import { Encounter } from '~/interfaces/encounters';

export default defineComponent({
  props: {
    encounter: { type: Object as PropType<Encounter>, required: true },
    size: { type: Number, default: 48 }
  },
  setup(props) {
    const icon = computed(() => `https://bungie.net${props.encounter.iconPath}`);

    return { icon };
  }
});
</script>

<style scoped>
img {
  background-image: url('data:image/jpeg;base64,');
  background-size: cover;
  background-repeat: no-repeat;
}

img[data-class='Hunter'] {
  background-image: url('data:image/jpeg;base64,');
